mapper.select:
select count(*) from AskQuestion where id = #{id}
and AskQuestionTitle like '%' || #{searchText} || '%' or AskQuestionContent like '%' || #{searchText} || '%'
and ${searchSelect} like '%' || #{searchText} || '%'

mapper.delete:
delete from
	AskQuestionReply
where
	AskQuestionReplyNum = #{AskQuestionReplyNum}
	and
	id = #{id}
