mapper.insert:
insert into
	memberbasic(
		memberNum
		, id
		, password
		, memberClass		
		, name
		, email
		, phone
		, address
		, postNum
		, deleteBy	

			, memberPicName
			, memberSaverPicName

	) values(
		membernum_seq.nextval
		, #{id}
		, #{password}
		, #{memberClass}		
		, #{name}
		, #{email}
		, #{phone}
		, #{address}
		, #{postNum}
		, #{deleteBy}	

			, #{memberPicName}
			, #{memberSaverPicName}

	)

mapper.update:
update memberbasic
	set
		password = #{password}
		, phone = #{phone}
		, email = #{email}
		, address = #{address}
	where
		id = #{id}
