mapper.insert:
insert into
	memberstaff(
		teacherNum
		, id 
		,inChargeAlumni 
		,inChargeSubject	
	) values(
		teacherNum_seq.nextval
		, #{id}
		, #{inChargeAlumni}
		, #{inChargeSubject}	
	)

mapper.update:
update memberstaff
	set
		inChargeAlumni = #{inChargeAlumni}
		, inChargeSubject = #{inChargeSubject}
	where
		id = #{id}

