mapper.insert:
insert into
	memberstudent(
		memberstudentNum
		, id 
		, alumni 
		, itMajor
		, jpMajor
		, classroom
		, groupNum 
		, seat 
		, late 
		, early 
		, absent 
    )values(
    	memberstudentNum_seq.nextval
    	, #{id}
    	, #{alumni}
    	, #{itMajor}
    	, #{jpMajor}
    	, #{classroom}
    	, #{groupNum}
		, #{seat}
		, #{late}
		, #{early}
		, #{absent}
	)

mapper.update:
update memberstudent
	set
		alumni = #{alumni}
		, itMajor = #{itMajor}
		, jpMajor = #{jpMajor}
	where
		id = #{id}
