mapper.insert:
insert into
	news(
		newsNum
		, id
		, targetAlumni
		, targetClass
		, newsTitle
		, newsHeader
		, newsContent
		, deleteBy

		, newsFileName
		, newsSavedFileName

	)values(
		newsNum_seq.nextval
		, #{id}
		, #{targetAlumni}
		, #{targetClass}
		, #{newsTitle}
		, #{newsHeader}
		, #{newsContent}
		, #{deleteBy}

		, #{newsFileName}
		, #{newsSavedFileName}	

	)

mapper.select:
select n.newsNum ,n.newsTitle ,n.newsHeader ,n.newsContent ,n.newsDate ,n.newsHits ,n.newsFileName ,n.newsSavedFileName ,m.alumni ,m.classroom from news n, memberstudent m
where targetAlumni = #{targetAlumni} AND targetClass = #{targetClass}
order by newsHeader desc , newsNum desc

select n.newsNum ,n.newsTitle ,n.newsHeader ,n.newsContent ,n.newsDate ,n.newsHits ,n.newsFileName ,n.newsSavedFileName ,m.alumni ,m.classroom from news n, memberstudent m
where targetAlumni = #{targetAlumni} AND targetClass = #{targetClass}
order by newsHeader desc , newsNum desc
