mapper.insert:
insert into
	news(
		newsNum
		, id
		, targetAlumni
		, targetClass
		, newsTitle
		, newsHeader
		, newsContent
		, deleteBy

		, newsFileName
		, newsSavedFileName

	)values(
		newsNum_seq.nextval
		, #{id}
		, #{targetAlumni}
		, #{targetClass}
		, #{newsTitle}
		, #{newsHeader}
		, #{newsContent}
		, #{deleteBy}

		, #{newsFileName}
		, #{newsSavedFileName}	

	)

mapper.select:
select newsNum ,newsTitle ,newsHeader ,newsContent ,newsDate ,newsHits , newsFileName , newsSavedFileName from news
where targetAlumni = #{targetAlumni} AND targetClass = #{targetClass}
order by newsNum desc
